import React, { Component } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Button from "@material-ui/core/Button";
import callApi from "../util/ApiCaller";
import { notification } from "antd";
import "antd/dist/antd.css";
import { Typography } from "@material-ui/core";

class DialogDelete extends Component {

  handleSubmit = (e) => {
    e.preventDefault();
    console.log("perform click reload buton");
    const { history, param } = this.props;
    console.log("param, history: ", param);
    callApi(`device?deviceHolderName=${param}`, "DELETE", null)
      .then((res) => {
        if (res.status === 200) {
          notification.success({
            message: "Success",
            description: "Delete devices successfully!",
          });
          this.props.setNeedRefreshTabMenuState(true);
          history.push("/");
        }
      })
      .catch((error) => {
        notification.error({
          message: "Error ",
          description: error.message,
        });
      });
  };

  render() {
    const {openDialog} = this.props;
    return (
      <div>
          {openDialog && (
            <Dialog
            open={this.props.openDialog}
            onClose={this.props.handleCloseDelete}
            aria-labelledby="form-dialog-title"
          >
            <DialogTitle id="form-dialog-title">{this.props.title}</DialogTitle>
            <DialogContent>
              <form onSubmit={this.handleSubmit} noValidate autoComplete="off">
                  <Typography variant="h6" gutterBottom>Are you sure you want to delete?</Typography>
                <DialogActions>
                  <Button onClick={this.props.handleCloseDelete} color="primary">
                    Cancel
                  </Button>
                  <Button
                    variant="contained"
                    type="submit"
                    color="primary"
                  >
                    Delete
                  </Button>
                </DialogActions>
              </form>
            </DialogContent>
          </Dialog>
          )}
      </div>
    );
  }
}

export default DialogDelete;
